import React, { useState, useEffect } from 'react';
import axios from 'axios';
function Wilaya() {
  const [wilayas, setWilayas] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWilayas = async () => {
      try {
        const token = 'Bearer ' + localStorage.getItem('jwt');
        const response = await axios.get('http://localhost:8000/api/wilaya/', {
          headers: {
            'Authorization': token,
            'Content-Type': 'application/json',
          },
        });
    
        setWilayas(response.data);
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des données:', error);
    
        if (error.response && error.response.status === 403) {
          setError('Accès refusé. Vérifiez vos informations d\'authentification ou vos autorisations.');
        } else {
          setError('Une erreur s\'est produite lors de la récupération des données.');
        }
      }
    };

    fetchWilayas();
  }, []);

  return (
    <div className="p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Liste des Wilayas</h2>

      {error && (
        <div className="text-red-500 mb-4">
          Erreur: {error}
        </div>
      )}

      <ul>
        {wilayas.map((wilaya) => (
          <li key={wilaya.id}>
            <strong>ID:</strong> {wilaya.id}, <strong>Nom:</strong> {wilaya.nom}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Wilaya;
