import React from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
    const handleLogout = async () => {
        try {
          const response = await fetch('http://127.0.0.1:8000/logout/', {
            method: 'POST',
            credentials: 'include',
          });
    
          if (response.ok) {
            // Déconnexion réussie
            console.log('Déconnexion réussie');
            localStorage.removeItem
            // Rediriger vers la page de connexion ou une autre page si nécessaire
          } else {
            // Gérer les erreurs de déconnexion
            console.error('Échec de la déconnexion');
          }
        } catch (error) {
          console.error('Erreur lors de la déconnexion:', error);
        }
      };
  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div>
        <Link to="/" className="text-xl font-bold">Mon Application</Link>
      </div>
      <div>
        <Link to="/logout" className="mr-4" onClick={handleLogout}>Déconnexion</Link>
        <Link to="/wilaya" className="mr-4">Wilaya</Link>
        <Link to="/profile">Profil</Link>
      </div>
    </nav>
  );
}

export default Navbar;
