import React, { useState, useEffect } from "react";

function Table() {
  const [donateurs, setDonateurs] = useState([]);
  const [filtreTypeSang, setFiltreTypeSang] = useState("");
  const [filtreWilaya, setFiltreWilaya] = useState("");

  useEffect(() => {
    fetchDonateur();
  }, []);

  const fetchDonateur = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/liste/");
      if (!response.ok) {
        throw new Error("Erreur lors de la récupération des données");
      }
      const data = await response.json();
      setDonateurs(data);
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  const filtrerDonateurs = () => {
    let filteredDonateurs = [...donateurs];
    if (filtreTypeSang) {
      filteredDonateurs = filteredDonateurs.filter(
        (donateur) => donateur.type_sang === filtreTypeSang
      );
    }
    if (filtreWilaya) {
      filteredDonateurs = filteredDonateurs.filter(
        (donateur) => donateur.wilaya === filtreWilaya
      );
    }
    return filteredDonateurs;
  };

  const handleTypeSangChange = (e) => {
    setFiltreTypeSang(e.target.value);
  };

  const handleWilayaChange = (e) => {
    setFiltreWilaya(e.target.value);
  };

  return (
    <section className="py-1 bg-blueGray-50">
      <div className="w-full xl:w-11/12 mb-12 xl:mb-0 px-4 mx-auto mt-24">
        <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded ">
          {/* Ajouter des sélecteurs pour les filtres */}
          <div className="flex justify-between px-4 py-3 border-b border-blueGray-200 bg-white rounded-t">
            <div>
              <label htmlFor="typeSang">Type de Sang:</label>
              <select
                id="typeSang"
                value={filtreTypeSang}
                onChange={handleTypeSangChange}
              >
                <option value="">Tous</option>
                <option value="A+">A+</option>
                <option value="A-">A-</option>
                {/* Ajoutez d'autres options selon vos besoins */}
              </select>
            </div>
            <div>
              <label htmlFor="wilaya">Wilaya:</label>
              <select
                id="wilaya"
                value={filtreWilaya}
                onChange={handleWilayaChange}
              >
                <option value="">Toutes</option>
                <option value="Alger">Alger</option>
                <option value="Oran">Oran</option>
                {/* Ajoutez d'autres options selon vos besoins */}
              </select>
            </div>
          </div>
          <div className="block w-full overflow-x-auto">
            <table className="items-center bg-transparent w-full border-collapse ">
              <thead>
                <tr>
                  <th> {/* Les en-têtes de colonne restent inchangés */} </th>
                </tr>
              </thead>
              <tbody>
                {/* Utiliser la fonction de filtrage pour afficher les donataires filtrés */}
                {filtrerDonateurs().map((donateur) => (
                  <tr key={donateur.id_d}>
                    <td>{/* Contenu de la ligne de tableau */}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Table;
