import React, { useState } from 'react';

const wilayaOptions = [
  "Adrar", "Assaba", "Brakna", "Dakhlet Nouadhibou", "Gorgol",
  "Guidimaka", "Hodh Ech Chargui", "Hodh El Gharbi", "Inchiri", 
  "Nouakchott-Nord", "Nouakchott-Ouest", "Nouakchott-Sud", 
  "Tagant", "Tiris Zemmour", "Trarza"
];

const typeSangOptions = [
  "A+", "A-", "B+", "B-", "AB+", "AB-", "O+", "O-"
];

function SignUpForm() {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    tel: '',
    username: '',
    email: '',
    wilaya: '',
    type_sang: '',
    password: '',
    password_confirm: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/api/register/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Une erreur s\'est produite lors de l\'inscription.');
      }

      console.log('Utilisateur inscrit avec succès.');
      // Réinitialiser le formulaire après inscription réussie si nécessaire
    } catch (error) {
      console.error('Erreur lors de l\'inscription:', error.message);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-blue-100">
      <div className="w-3/4 bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <h2 className="text-2xl font-bold mb-4 text-blue-700">Inscription</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="flex flex-wrap justify-between">
            <div className="w-full md:w-1/2 mb-4">
              <label htmlFor="first_name" className="block font-semibold">Prénom</label>
              <input
                type="text"
                id="first_name"
                name="first_name"
                value={formData.first_name}
                onChange={handleChange}
                required
                className="input-field"
              />
            </div>
            {/* Ajoutez d'autres champs similaires ici */}
            <div className="w-full md:w-1/2 mb-4">
              <label htmlFor="type_sang" className="block font-semibold">Type de sang</label>
              <select
                id="type_sang"
                name="type_sang"
                value={formData.type_sang}
                onChange={handleChange}
                required
                className="input-field"
              >
                <option value="">Sélectionnez un type de sang</option>
                {typeSangOptions.map((typeSang, index) => (
                  <option key={index} value={typeSang}>{typeSang}</option>
                ))}
              </select>
            </div>
            <div className="w-full md:w-1/2 mb-4">
              <label htmlFor="wilaya" className="block font-semibold">Wilaya</label>
              <select
                id="wilaya"
                name="wilaya"
                value={formData.wilaya}
                onChange={handleChange}
                required
                className="input-field"
              >
                <option value="">Sélectionnez une wilaya</option>
                {wilayaOptions.map((wilaya, index) => (
                  <option key={index} value={wilaya}>{wilaya}</option>
                ))}
              </select>
            </div>
          </div>
          <div className="flex justify-end">
            <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600">S'inscrire</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignUpForm;
