import React, { useState, useEffect } from 'react';

import axios from 'axios';

function Wilaya(){
  const [wilayas, setWilayas] = useState([]);

  useEffect(() => {
    // Fonction pour récupérer les données de l'API
    const fetchWilayas = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/wilaya/');
        setWilayas(response.data);
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des données:', error);
      }
    };

    // Appel de la fonction pour récupérer les données lors du montage du composant
    fetchWilayas();
  }, []); // Le tableau vide en second argument assure que useEffect ne s'exécute qu'une fois au montage du composant

  

  return (
    <div className="p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Liste des Wilayas</h2>
      <ul>
        {wilayas.map(wilaya => (
          <li key={wilaya.id}>
            {wilaya.id} - {wilaya.nom}
          </li>
        ))}
      </ul>
    </div>
  );
  
};

export default Wilaya;
