import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Table/navbar';
import HelloWorld from './Table/helloworld';
import Wilaya from './Table/wilaya';
import './App.css';

function App() {
  // État pour la gestion de la connexion
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Fonction pour se connecter
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Fonction pour se déconnecter
  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        {/* Boutons de connexion/déconnexion en dehors de la barre de navigation */}
        {!isLoggedIn ? (
          <button className="btn btn-primary" onClick={handleLogin}>Se connecter</button>
        ) : (
          <button className="btn btn-danger" onClick={handleLogout}>Se déconnecter</button>
        )}
        {/* Barre de navigation */}
        <Navbar isLoggedIn={isLoggedIn} />
        <div className="mt-20"></div>
        <Routes>
          <Route path="/" element={<HelloWorld />} />
          <Route path="/wilaya" element={<Wilaya />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
