import React, { useState,useContext } from 'react';
import AuthContext from '../session';
function UpdatePasswordForm() {
  const [passwords, setPasswords] = useState({ currentPassword: '', newPassword: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  let {authTokens,logout} = useContext(AuthContext);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPasswords(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation des champs
    if (passwords.newPassword !== passwords.confirmPassword) {
      setError("Les mots de passe ne correspondent pas.");
      return;
    }

    // Requête vers l'API
    try {
        const acces = "Bearer " + String(authTokens.token);
        const response = await fetch("http://127.0.0.1:8000/user/", {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: acces,
          },
          body: JSON.stringify({
            password_confirm: passwords.currentPassword,
            password: passwords.newPassword
          }),
        });

      if (!response.ok) {
        throw new Error('Erreur lors de la mise à jour du mot de passe.');
      }

      setSuccess("Mot de passe mis à jour avec succès.");
      setPasswords({ currentPassword: '', newPassword: '', confirmPassword: '' });
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}
      <form onSubmit={handleSubmit}>
        <label>
          Mot de passe actuel:
          <input type="password" name="currentPassword" value={passwords.currentPassword} onChange={handleChange} />
        </label>
        <label>
          Nouveau mot de passe:
          <input type="password" name="newPassword" value={passwords.newPassword} onChange={handleChange} />
        </label>
        <label>
          Confirmer le nouveau mot de passe:
          <input type="password" name="confirmPassword" value={passwords.confirmPassword} onChange={handleChange} />
        </label>
        <button type="submit">Mettre à jour le mot de passe</button>
      </form>
    </div>
  );
}

export default UpdatePasswordForm;
