import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useSession } from '../session';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
        credentials: 'include',
      });

      const data = await response.json();

      if (response.ok) {
        // Lecture du cookie depuis la réponse
        const jwtCookie = document.cookie.split('; ').find(row => row.startsWith('jwt=')).split('=')[1];

        // Stocker le token dans le localStorage
        localStorage.setItem('jwt', jwtCookie);

        // Rediriger l'utilisateur vers une autre page après une connexion réussie
        navigate('/wilaya');
      } else {
        setError(data.detail);
      }
    } catch (error) {
      console.error('Erreur lors de la requête:', error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          {/* Votre formulaire */}
        </form>
      </div>
    </div>
  );
}

export default Login;
