import React from 'react';
import { useNavigate,Link } from 'react-router-dom';
import { useSession } from '../session'; // Import du hook useSession

function Navbar() {
  const { isLoggedIn, logout } = useSession(); // Utilisation du hook useSession
  const navigate = useNavigate();

  const handleLogout = async () => {
    logout();
    localStorage.removeItem('jwt');
    navigate("/");
  };

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div>
        <Link to="#" className="text-xl font-bold">Mon Application</Link>
      </div>
      <div>
        <Link to="" className="mr-4" >Home</Link>
        {!isLoggedIn && (
        <Link to="/login" className="mr-4" >Login</Link>
        )}
        {!isLoggedIn && (
        <Link to="#" className="mr-4" >Register</Link>
        )}
        {/* Rendre le champ "Logout" invisible si l'utilisateur n'est pas connecté */}
        {isLoggedIn && (
          <Link to="/logout" className="mr-4" onClick={handleLogout}>Déconnexion</Link>
        )}
        {isLoggedIn && (
        <Link to="/wilaya" className="mr-4">Wilaya</Link>
        )}
        {isLoggedIn && (
        <Link to="/profile">Profil</Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
