import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useSession } from '../session';

function Login() {
  const { isLoggedIn, login } = useSession();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
        credentials: 'include',
      });

      const data = await response.json();

      localStorage.setItem('jwt', data.jwt);

      if (response.ok) {
        login(); // Appeler la fonction de connexion du contexte de session
        navigate('/wilaya');
      } else {
        setError(data.detail);
      }
    } catch (error) {
      console.error('Erreur lors de la requête:', error.message);
    }
  };

  if (isLoggedIn) {
    navigate('/wilaya');
  }

  return (
    
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">
              Mot de passe
            </label>
            <input
              type={showPassword ? 'text' : 'password'} // Condition pour afficher ou masquer le mot de passe
              id="password"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button
              type="button"
              onClick={() => setShowPassword(!showPassword)} // Inversion de l'état pour afficher ou masquer le mot de passe
              className="text-gray-600 hover:text-gray-700 focus:outline-none focus:text-gray-700 mt-2"
            >
              {showPassword ? 'Masquer' : 'Afficher'} le mot de passe
            </button>
          </div>
          {/* Reste du formulaire */}
        </form>
      </div>
    </div>
  );
}

export default Login;
