import React from 'react';
import { Link } from 'react-router-dom';
import { useSession } from './session';

function Navbar() {
  const { isLoggedIn, logout } = useSession();

  const handleLogout = () => {
    // Effacer le token de l'utilisateur et le déconnecter
    localStorage.removeItem('jwt');
    logout();
  };

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div>
        <Link to="/" className="text-xl font-bold">Mon Application</Link>
      </div>
      <div>
        {isLoggedIn ? (
          <>
            <Link to="/wilaya" className="mr-4">Wilaya</Link>
            <Link to="/" onClick={handleLogout}>Déconnexion</Link>
          </>
        ) : (
          <>
            <Link to="/login" className="mr-4">Connexion</Link>
            <Link to="/inscription" className="mr-4">Inscription</Link>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
