import React, { useState } from 'react';

function FormulaireDepistage() {
  const [reponses, setReponses] = useState({
    fièvre: false,
    voyage: false,
    rapportsSexuels: false,
    transfusionSanguine: false,
    maladieInfectieuse: false,
    médicaments: false
  });

  const handleChange = (e) => {
    const { name, checked } = e.target;
    setReponses(prevReponses => ({
      ...prevReponses,
      [name]: checked
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Envoyer les réponses à votre API ou effectuer une validation supplémentaire ici
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Avez-vous eu de la fièvre au cours des deux dernières semaines ?
        <input
          type="checkbox"
          name="fièvre"
          checked={reponses.fièvre}
          onChange={handleChange}
        />
      </label>
      <label>
        Avez-vous voyagé dans une zone touchée par une maladie infectieuse au cours du dernier mois ?
        <input
          type="checkbox"
          name="voyage"
          checked={reponses.voyage}
          onChange={handleChange}
        />
      </label>
      <label>
        Avez-vous eu des rapports sexuels non protégés avec un nouveau partenaire au cours des trois derniers mois ?
        <input
          type="checkbox"
          name="rapportsSexuels"
          checked={reponses.rapportsSexuels}
          onChange={handleChange}
        />
      </label>
      <label>
        Avez-vous déjà eu une transfusion sanguine ou reçu des produits sanguins au cours de votre vie ?
        <input
          type="checkbox"
          name="transfusionSanguine"
          checked={reponses.transfusionSanguine}
          onChange={handleChange}
        />
      </label>
      <label>
        Avez-vous déjà été diagnostiqué avec une maladie infectieuse transmissible par le sang, telle que le VIH ou l'hépatite ?
        <input
          type="checkbox"
          name="maladieInfectieuse"
          checked={reponses.maladieInfectieuse}
          onChange={handleChange}
        />
      </label>
      <label>
        Prenez-vous actuellement des médicaments qui pourraient affecter la qualité de votre sang ?
        <input
          type="checkbox"
          name="médicaments"
          checked={reponses.médicaments}
          onChange={handleChange}
        />
      </label>
      <button type="submit">Envoyer</button>
    </form>
  );
}

export default FormulaireDepistage;
