import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useSession } from '../session';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useSession();
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Effectuer une requête de connexion à votre API
      // Ici, vous pouvez envoyer les données d'identification (email, mot de passe) à votre API
      // Gérez la réponse de l'API en conséquence
      // Si la connexion réussit, vous pouvez stocker les informations de session ou rediriger l'utilisateur vers une autre page
      // Si la connexion échoue, affichez un message d'erreur à l'utilisateur

      // Exemple simplifié
      if (email === 'user@example.com' && password === 'password') {
        // Si la connexion réussit, stockez les informations de session et redirigez l'utilisateur
        login({ email }); // Stockage des informations de session (dans ce cas, l'email)
        navigate('/dashboard'); // Redirection vers la page de tableau de bord
      } else {
        // Si la connexion échoue, affichez un message d'erreur
        setError('Adresse email ou mot de passe incorrect.');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion :', error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          {/* Champ Email */}
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          {/* Champ Mot de passe */}
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">
              Mot de passe
            </label>
            <input
              type="password"
              id="password"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {/* Bouton Connexion */}
          <div className="flex items-center justify-between mb-4">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Connexion
            </button>
          </div>
          {/* Affichage des erreurs */}
          {error && (
            <div className="border border-red-500 bg-pink-100 rounded p-4 mb-4">
              <p className="text-red-500 text-sm">{error}</p>
            </div>
          )}
          {/* Lien vers la page d'inscription */}
          <div className="text-center">
            <p className="text-gray-700">Vous n'avez pas de compte ?</p>
            <Link to="/inscription" className="text-blue-500 hover:text-blue-800">
              Créer un compte
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
