import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function HelloWorld() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [error, setError] = useState(null);
  const [token, setToken] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
        credentials: 'include',
      });

      // Handle the response
      const data = await response.json();

      // Store the token in localStorage
      localStorage.setItem('jwt', data.jwt);

      if (response.ok) {
        // Set the token state variable
        setToken(data.jwt);
        console.log(data.jwt);
        // Redirect the user to another page after successful login
        // navigate('/wilaya');
      } else {
        setError(data.detail);
      }
    } catch (error) {
      console.error('Error during request:', error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">
              Email
            </label>

            <input
              type="email"
              id="email"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">
              Mot de passe
            </label>
            <input
              type="password"
              id="password"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="flex items-center justify-between mb-4">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Connexion
            </button>
          </div>
          {token && ( // Render token if it exists
            <div className="border border-green-500 bg-green-100 rounded p-4 mb-4">
              <p className="text-green-500 text-sm">Token: {token}</p>
            </div>
          )}
          {error && (
            <div className="border border-red-500 bg-pink-100 rounded p-4 mb-4">
              <p className="text-red-500 text-sm">{error}</p>
            </div>
          )}
          <div className="text-center">
            <p className="text-gray-700">Vous n'avez pas de compte ?</p>
            <Link to="/inscription" className="text-blue-500 hover:text-blue-800">
              Cr√©er un compte
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default HelloWorld;
