import React, { useState, useEffect } from 'react';
import Navbar  from './navbar';
function Wilaya() {
  const [wilayas, setWilayas] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWilayas = async () => {
      try {
        // Remplacez 'votre_api_key' par votre logique pour récupérer le token JWT
        const token = 'Bearer ' + localStorage.getItem('jwt');
        console.log(token);
        const response = await fetch('http://localhost:8000/api/wilaya/', {
          headers: {
            'Authorization': token,
            //'Content-Type': 'application/json',
          },
          credentials:'include'
        });

        if (!response.ok) {
          throw new Error('La requête a échoué avec le statut ' + response.status);
        }

        const data = await response.json();
        setWilayas(data);
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des données:', error.message);
        setError('Une erreur s\'est produite lors de la récupération des données.');
      }
    };

    fetchWilayas();
  }, []);

  return (
    
    <div className="p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Liste des Wilayas</h2>

      {error && (
        <div className="text-red-500 mb-4">
          Erreur: {error}
        </div>
      )}

      <ul>
        {wilayas.map((wilaya) => (
          <li key={wilaya.id}>
            <strong>ID:</strong> {wilaya.id}, <strong>Nom:</strong> {wilaya.nom}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Wilaya;
