import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function HelloWorld() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
        credentials: 'include',
      });

      // Traitez la réponse ici
      const data = await response.json();

      if (response.ok) {
        console.log(data.jwt);
        // Redirigez l'utilisateur vers une autre page après une connexion réussie
        // navigate('/wilaya');
      } else {
        setError(data.detail);
      }
    } catch (error) {
      console.error('Erreur lors de la requête:', error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          {/* ... (autres champs de formulaire) */}
          <div className="flex items-center justify-between mb-4">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Connexion
            </button>
          </div>
          {error && (
            <div className="border border-red-500 bg-pink-100 rounded p-4 mb-4">
              <p className="text-red-500 text-sm">{error}</p>
            </div>
          )}
          <div className="text-center">
            <p className="text-gray-700">Vous n'avez pas de compte ?</p>
            <Link to="/inscription" className="text-blue-500 hover:text-blue-800">
              Créer un compte
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default HelloWorld;
