import React, { useState, useEffect,useContext } from "react";
import axios from "axios";
import AuthContext from '../session';
function UserProfile() {
  const [userData, setUserData] = useState(null);
  let { authTokens } = useContext(AuthContext);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const acces = "Bearer " + String(authTokens.token);
        const response = await axios.get("http://127.0.0.1:8000/user/", {
          headers: {
            Authorization: acces,
          },

          credentials: "include",
        });
        setUserData(response.data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des données utilisateur :",
          error
        );
      }
    };

    fetchUserData();
  }, []);

  if (!userData) {
    return <p>Chargement des données...</p>;
  }

  return (
    <div>
      <h1>Profil Utilisateur</h1>
      <p>Prénom: {userData.first_name}</p>
      <p>Nom: {userData.last_name}</p>
      <p>Téléphone: {userData.tel}</p>
      <p>Email: {userData.email}</p>
      <p>Date de naissance: {userData.date_naissance}</p>
      <p>Wilaya: {userData.wilaya}</p>
      <p>Type de sang: {userData.type_sang}</p>
    </div>
  );
}

export default UserProfile;
