import React, { createContext, useContext, useState } from 'react';

// Créer un contexte pour les données de session
export const SessionContext = createContext(null);

// Hook personnalisé pour accéder aux données de session dans les composants
export const useSession = () => useContext(SessionContext);

// Composant de contexte pour gérer les données de session
export const SessionProvider = ({ children }) => {
  // État pour suivre l'état de connexion de l'utilisateur
  const [isLoggedIn, setIsLoggedIn] = useState(null);

  // Fonction pour mettre à jour l'état de connexion de l'utilisateur lorsqu'il se connecte
  const login = (to) => {
    setIsLoggedIn(true);
    // Effectuer d'autres actions comme stocker le token JWT
  };

  // Fonction pour mettre à jour l'état de connexion de l'utilisateur lorsqu'il se déconnecte
  const logout = () => {
    setIsLoggedIn(false);
    // Effacer les données de session, par exemple le token JWT
  };

  // Autres fonctions ou états liés à la session...

  return (
    <SessionContext.Provider value={{ isLoggedIn, login, logout }}>
      {children}
    </SessionContext.Provider>
  );
};
