import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HelloWorld() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ajoutez ici la logique d'authentification avec votre backend
    console.log('Email:', email, 'Password:', password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">Email</label>
            <input
              type="email"
              id="email"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">Mot de passe</label>
            <input
              type="password"
              id="password"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Connexion
            </button>
            <a href="#" className="text-blue-500 hover:text-blue-800">Mot de passe oubli√© ?</a>
          </div>
        </form>
      </div>
    </div>
  );
};

export default HelloWorld;
