import React, { useState, useEffect } from 'react';
import { useSession } from '../session';

function WilayaSelector({ onSelect }) {
  const [wilayas, setWilayas] = useState([]);
  const [error, setError] = useState(null);
  const { isLoggedIn } = useSession();

  useEffect(() => {
    const fetchWilayas = async () => {
      try {
        const token = 'Bearer ' + localStorage.getItem('jwt');
        const response = await fetch('http://localhost:8000/api/wilaya/', {
          headers: {
            'Authorization': token,
          },
          credentials:'include'
        });

        if (!response.ok) {
          throw new Error('La requête a échoué avec le statut ' + response.status);
        }

        const data = await response.json();
        setWilayas(data);
      } catch (error) {
        console.error('Une erreur s\'est produite lors de la récupération des données:', error.message);
        setError('Une erreur s\'est produite lors de la récupération des données.');
      }
    };

    fetchWilayas();
  }, []);

  const handleSelectChange = (event) => {
    const selectedWilaya = wilayas.find(wilaya => wilaya.id === parseInt(event.target.value));
    onSelect(selectedWilaya);
  };

  return (
    <div className="p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Sélectionnez une Wilaya</h2>

      {error && (
        <div className="text-red-500 mb-4">
          Erreur: {error}
        </div>
      )}

      <select onChange={handleSelectChange} className="border border-gray-300 rounded p-2">
        <option value="">Sélectionnez une wilaya</option>
        {wilayas.map((wilaya) => (
          <option key={wilaya.id} value={wilaya.id}>{wilaya.nom}</option>
        ))}
      </select>
    </div>
  );
}

export default WilayaSelector;
