import React, { useState } from 'react';

function App() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);

  const login = async (credentials) => {
    try {
      const response = await fetch('http://127.0.0.1:8000/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
        credentials: 'include', // Inclure les cookies dans la requête
      });

      const data = await response.json();

      if (response.ok) {
        // Si la requête est réussie, le serveur renvoie un objet contenant le token
        // Stockez le token dans le state ou localStorage selon votre besoin
        console.log(data.jwt);
      } else {
        // Si la requête échoue, le serveur renvoie un objet contenant l'erreur
        setError(data.detail);
      }
    } catch (error) {
      console.error('Erreur lors de la requête:', error.message);
    }
  };

  const handleLogin = () => {
    const credentials = {
      email: '',
      password: 'votre_mot_de_passe',
    };

    login(credentials);
  };

  return (
    <div>
      <h1>Mon Application React</h1>
      <button onClick={handleLogin}>Se connecter</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default App;
