import React, { useState,useEffect } from "react";
//import { useSession } from "../session";
import { Link, useNavigate } from "react-router-dom";

function SignUphtmlForm() {
  const navigate = useNavigate();
  const [wilayas, setWilayas] = useState([]);
  const [sangs, setSangs] = useState([]);
  const [htmlFormData, sethtmlFormData] = useState({
    first_name: "",
    last_name: "",
    tel: "",
    email: "",
    wilaya: "",
    type_sang: "",
    password: "",
    password_confirm: "",
  });

  useEffect(() => {
    fetchWilayas();
    fetchSangs();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    sethtmlFormData({
      ...htmlFormData,
      [name]: value,
    });
  };
  const fetchSangs = async () => {
    const response = await fetch("http://127.0.0.1:8000/sang/");
    const data = await response.json();
    setSangs(data);
  };

  const fetchWilayas = async () => {
    const response = await fetch("http://127.0.0.1:8000/wilaya/");
    const data = await response.json();
    setWilayas(data);
  };

  // if (isLoggedIn) {
  //   navigate("/");
  // }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/register/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(htmlFormData),
      });

      if (!response.ok) {
        // Si la réponse n'est pas OK, lancer une erreur avec le message d'erreur renvoyé par l'API
        const errorMessage = await response.text();
        throw new Error(
          errorMessage || "Une erreur s'est produite lors de l'inscription."
        );
      }

      // Si la réponse est OK, rediriger l'utilisateur vers une autre page par exemple
      navigate("/", { replace: true });
      console.log("Utilisateur inscrit avec succès.");

      // Réinitialiser le htmlFormulaire après inscription réussie si nécessaire
    } catch (error) {
      console.error("Erreur lors de l'inscription:", error.message);
      // Traitez l'erreur ici, par exemple, affichez un message d'erreur à l'utilisateur
    }
  };

  return (
    <div className="w-3/4 mx-auto">
      <div>
        <h2 className="text-xl font-bold mb-4">Inscription</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="flex flex-row justify-between space-x-[2rem]">
            <div className="flex flex-col w-[70%]">
              <label htmlFor="first_name" className="mb-1">
                Prénom
              </label>
              <input
                type="text"
                id="first_name"
                name="first_name"
                value={htmlFormData.first_name}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md w-full"
              />
            </div>

            <div className="flex flex-col w-[70%]">
              {" "}
              <label htmlFor="last_name" className="mb-1">
                Nom de famille
              </label>
              <input
                type="text"
                id="last_name"
                name="last_name"
                value={htmlFormData.last_name}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              />
            </div>
          </div>
          <div className="flex flex-row justify-between space-x-[2rem]">
            <div className="flex flex-col w-[70%]">
              <label htmlFor="email" className="mb-1">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={htmlFormData.email}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              />
            </div>
            <div className="flex flex-col w-[70%]">
              <label htmlFor="tel" className="mb-1">
                Téléphone
              </label>
              <input
                type="tel"
                id="tel"
                name="tel"
                value={htmlFormData.tel}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              />
            </div>
          </div>
          <div className="flex flex-row justify-between space-x-[2rem]">
            <div className="flex flex-col w-[70%]">
              <label htmlFor="wilaya" className="mb-1">
                Wilaya
              </label>
              <select
                id="sang"
                value={htmlFormData.wilaya}
                onChange={handleChange}
                className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
              >
                <option value="">Toutes Les Types Sangs</option>
                {sangs.map((sang) => (
                  <option key={sang.id} value={sang.nom}>
                    {sang.nom}
                  </option>
                ))}
                {/* Ajoutez d'autres options selon vos besoins */}
              </select>
            </div>
            <div className="flex flex-col w-[70%]">
              <label htmlFor="type_sang" className="mb-1">
                Type de sang
              </label>
              <select
                      id="sang"
                      value={htmlFormData.type_sang}
                      onChange={(event) => setFiltreWilaya(event.target.value)}
                      className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    >
                      <option value="">Toutes Les Wilayas</option>
                      {wilayas.map((wilaya) => (
                        <option key={wilaya.nom} value={wilaya.nom}>
                          {wilaya.nom}
                        </option>
                      ))}
                      {/* Ajoutez d'autres options selon vos besoins */}
                    </select>
              <select
                id="type_sang"
                name="type_sang"
                value={htmlFormData.type_sang}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              >
                <option value="">Sélectionnez un type de sang</option>
                {Object.entries(typeSangOptions).map(([typeSang, value]) => (
                  <option key={value} value={value}>
                    {typeSang}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="flex flex-row justify-between space-x-[2rem]">
            <div className="flex flex-col w-[50%]">
              <label htmlFor="password" className="mb-1">
                Mot de passe
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={htmlFormData.password}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              />
            </div>
            <div className="flex flex-col w-[50%]">
              <label htmlFor="password_confirm" className="mb-1">
                Confirmer le mot de passe
              </label>
              <input
                type="password"
                id="password_confirm"
                name="password_confirm"
                value={htmlFormData.password_confirm}
                onChange={handleChange}
                required
                className="px-3 py-2 border rounded-md"
              />
            </div>
          </div>
          <div className="mt-6 flex items-center justify-end gap-x-6">
            <button
              type="submit"
              className="bg-blue-500 text-white font-semibold py-2 px-4 rounded-md hover:bg-blue-600 transition-colors"
            >
              S'inscrire
            </button>
            <button className="text-black font-semibold py-2 px-4 rounded-md hover:bg-gray-600 transition-colors">
              <Link to="/">Annuler</Link>
            </button>
            <Link />
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignUphtmlForm;
