import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Fonction pour récupérer les données de l'utilisateur depuis l'API
    const fetchUserData = async () => {
      try {
        const token = 'Bearer ' + localStorage.getItem('jwt');
        const response = await axios.get('url_de_votre_api/user/', {
          headers: {
            Authorization: token,
          },
        });
        setUserData(response.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données utilisateur :', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div>
      <h1>Profil Utilisateur</h1>
      {userData ? (
        <div>
          <p>Prénom: {userData.first_name}</p>
          <p>Nom: {userData.last_name}</p>
          <p>Téléphone: {userData.tel}</p>
          <p>Email: {userData.email}</p>
          <p>Date de naissance: {userData.date_naissance}</p>
          <p>Wilaya: {userData.wilaya}</p>
          <p>Type de sang: {userData.type_sang}</p>
        </div>
      ) : (
        <p>Chargement des données...</p>
      )}
    </div>
  );
}

export default UserProfile;
