import React,{useEffect,useState,useContext} from "react";
import AgeCalculator from "./tabledon";
import { useParams } from 'react-router-dom'; 
import AuthContext from "../session";
const Profile = () => {
  const [userData, setUserData] = useState(null);
  let { Tokens,logout } = useContext(AuthContext);
  const {id}=useParams();
  useEffect(() => {
    const fetchUserData = async () => {
      //const acces = "Bearer " + String(authTokens.token);
      const response = await fetch(`http://127.0.0.1:8000/don/${id}/`, {
        method: "GET",
        // headers: {
        //   Authorization: acces,
        // },

        credentials: "include",
      });
      const data = await response.json();
      setUserData(data);
      // if (response.status === 200) {
      //   setUserData(data);
      // } else {
      //   logout();
      // }
      
    };

    fetchUserData();
  }, []);
  return (
    <div class="p-16">
      <div class="p-8 bg-white shadow mt-24">
        <div class="grid grid-cols-1 md:grid-cols-3">
          <div class="grid grid-cols-3 text-center order-last md:order-first mt-20 md:mt-0">
            <button class="text-white py-2 px-4 uppercase rounded bg-gray-700 hover:bg-gray-800 shadow hover:shadow-lg font-medium transition transform hover:-translate-y-0.5">
              Message
            </button>
          </div>
          <div class="relative">
            <div class="w-48 h-48 bg-indigo-100 mx-auto rounded-full shadow-2xl absolute inset-x-0 top-0 -mt-24 flex items-center justify-center text-indigo-500">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                class="h-24 w-24"
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path
                  fill-rule="evenodd"
                  d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                  clip-rule="evenodd"
                />
              </svg>
            </div>
          </div>

          <div class="space-x-8 flex justify-between mt-32 md:mt-0 md:justify-center">
            <button class="text-white py-2 px-4 uppercase rounded bg-blue-400 hover:bg-blue-500 shadow hover:shadow-lg font-medium transition transform hover:-translate-y-0.5">
              Connect
            </button>
          </div>
        </div>

        <div class="mt-20 text-center border-b pb-12">
          <h1 class="text-4xl font-medium text-gray-700">
            {userData.first_name} {userData.last_name}, <span class="font-light text-gray-500"><AgeCalculator dateOfBirth={userData.date_naissance}/></span>
          </h1>
          <p class="font-light text-gray-600 mt-3">Bucharest, Romania</p>

          <p class="mt-8 text-gray-500">
            Solution Manager - Creative Tim Officer
          </p>
          <p class="mt-2 text-gray-500">University of Computer Science</p>
        </div>

        <div class="mt-12 flex flex-col justify-center">
          <p class="text-gray-600 text-center font-light lg:px-16">
            An artist of considerable range, Ryan — the name taken by
            Melbourne-raised, Brooklyn-based Nick Murphy — writes, performs and
            records all of his own music, giving it a warm, intimate feel with a
            solid groove structure. An artist of considerable range.
          </p>
          <button class="text-indigo-500 py-2 px-4  font-medium mt-4">
            Show more
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
