import React, { useState } from 'react';

function FormulaireDepistage() {
  const [reponses, setReponses] = useState({
    fièvre: false,
    voyage: false,
    rapportsSexuels: false,
    transfusionSanguine: false,
    maladieInfectieuse: false,
    médicaments: false
  });

  const handleChange = (e) => {
    const { name, checked } = e.target;
    setReponses(prevReponses => ({
      ...prevReponses,
      [name]: checked
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Envoyer les réponses à votre API ou effectuer une validation supplémentaire ici
  };

  return (
    <div className="max-w-lg mx-auto">
      <h1 className="text-3xl font-bold mb-4">Formulaire de Dépistage</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <QuestionCheckbox
          label="Avez-vous eu de la fièvre au cours des deux dernières semaines ?"
          name="fièvre"
          checked={reponses.fièvre}
          onChange={handleChange}
        />
        <QuestionCheckbox
          label="Avez-vous voyagé dans une zone touchée par une maladie infectieuse au cours du dernier mois ?"
          name="voyage"
          checked={reponses.voyage}
          onChange={handleChange}
        />
        <QuestionCheckbox
          label="Avez-vous eu des rapports sexuels non protégés avec un nouveau partenaire au cours des trois derniers mois ?"
          name="rapportsSexuels"
          checked={reponses.rapportsSexuels}
          onChange={handleChange}
        />
        <QuestionCheckbox
          label="Avez-vous déjà eu une transfusion sanguine ou reçu des produits sanguins au cours de votre vie ?"
          name="transfusionSanguine"
          checked={reponses.transfusionSanguine}
          onChange={handleChange}
        />
        <QuestionCheckbox
          label="Avez-vous déjà été diagnostiqué avec une maladie infectieuse transmissible par le sang, telle que le VIH ou l'hépatite ?"
          name="maladieInfectieuse"
          checked={reponses.maladieInfectieuse}
          onChange={handleChange}
        />
        <QuestionCheckbox
          label="Prenez-vous actuellement des médicaments qui pourraient affecter la qualité de votre sang ?"
          name="médicaments"
          checked={reponses.médicaments}
          onChange={handleChange}
        />
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Envoyer</button>
      </form>
    </div>
  );
}

function QuestionCheckbox({ label, name, checked, onChange }) {
  return (
    <label className="flex items-center">
      <input
        type="checkbox"
        name={name}
        checked={checked}
        onChange={onChange}
        className="mr-2"
      />
      <span>{label}</span>
    </label>
  );
}

export default FormulaireDepistage;
