import React, { useState } from 'react';
import axios from 'axios';

function HelloWorld() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); // State to hold error message


  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/login/', {
        email: email,
        password: password
      });

      // Traitez la réponse ici, par exemple, redirigez l'utilisateur ou stockez le jeton d'authentification
      console.log(response.data);
    } catch (error) {
      // Gérez l'erreur ici
      if (error.response) {
        // La requête a été faite et le serveur a répondu avec un code d'erreur
        console.error('Erreur de réponse du serveur:', error.response.data);
        setError(error.response.data.detail);
      } else if (error.request) {
        // La requête a été faite, mais aucune réponse n'a été reçue
        console.error('Aucune réponse du serveur reçue.');
      } else {
        // Une erreur s'est produite lors de la configuration de la requête
        console.error('Erreur lors de la configuration de la requête :', error.message);
      }
    }
  
  
  };
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-3xl font-semibold mb-6 text-center">Connexion</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">Email</label>

            <input
              type="email"
              id="email"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">Mot de passe</label>
            <input
              type="password"
              id="password"
              className="w-full border-gray-300 rounded-lg shadow-sm px-3 py-2 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="Entrez votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && (
            <p className="text-red-500 text-sm mb-4">{error}</p>
          )}
          <div className="flex items-center justify-between mb-4">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Connexion
            </button>
            {/* <a href="#" className="text-blue-500 hover:text-blue-800">Mot de passe oublié ?</a> */}
          </div>
          <div className="text-center">
            <p className="text-gray-700">Vous n'avez pas de compte ?</p>
            <a href="/inscription" className="text-blue-500 hover:text-blue-800">Créer un compte</a>
          </div>
        </form>
      </div>
    </div>
  );
};

export default HelloWorld;
